import React, { useState } from 'react';
import './App.css';

function App() {
  const [current, setScene] = useState("menu");

  var gameData = initScenes();
  var currentScene = gameData.get(current);
  console.log(currentScene);
  return(
    <div className="App">
      {currentScene}
    <button
        type="button"
        onClick={() => setScene("catalog")}
      >Blue</button>
    </div>
  );
}

function initScenes() {
  var gameData = new Map();
  const allScenes = new Set(["menu", "catalog", "editor", "finale"]);
  allScenes.forEach(element => {
    gameData.set(element, <Scene type={element} />);
  });
  return gameData;
}

class Scene extends React.Component {
  constructor(type) {
    super();
  }

  render() {
    console.log(this.props.type);
    switch(this.props.type) {
      case "menu":
        console.log("meni?");
        return this.generateMenu();
      case "catalog":
        return generateCatalog();
      case "editor":
        return generateEditor();
      case "finale":
        return generateFinale();
      default:
        return;
    }
  }

  generateMenu() {
    console.log("gthduf");
    return(
    <div className="titleScreen">
      <h1>Styledollz 2.0</h1>
      <button onClick={this.dismount()}>Start!</button>
    </div>
    );
  }
}
