import React, { useState } from 'react';
import './App.css';

function App() {
  var gameData = initScenes();
  let tempScene = gameData.get("menu");
  tempScene.state.display = true;
  gameData.set("menu", tempScene);
  let sceneScreen;
  gameData.forEach(number => {
    if (number.state.display) {
      sceneScreen = <Scene />;
    };
  });

  return(
    <div className="App">
      {sceneScreen}
    </div>
  );
}

function initScenes() {
  var gameData = new Map();
  const allScenes = new Set(["menu", "catalog", "editor", "finale"]);
  allScenes.forEach(element => {
    gameData.set(element, new Scene(element));
  });
  return gameData;
}

class Scene extends React.Component {
  constructor(type) {
    super();
    this.state = {
      display: false,
    };
    this.name = type;
  }

  dismount() {
    this.setState({display: false});
  }

  render() {
    switch(this.name) {
      case "menu":
        return this.generateMenu();
      case "catalog":
        return this.generateCatalog();
      case "editor":
        return this.generateEditor();
      case "finale":
        return this.generateFinale();
      default:
        return;
    }
  }

  /**
  * Present the main menu
  */
  generateMenu(gameData) {
    return(
    <div className="titleScreen">
      <h1>Styledollz 2.0</h1>
      <button onClick={() => changeScene(gameData, "catalog")}>Start!</button>
    </div>
    );
  }

  /**
   * Present the catalog and canvas
   */
  generateCatalog(gameData) {
    return(
      <div className="titleScreen">
        <h1>BIRTCHES</h1>
      </div>
    );
  }

  /**
   * Present canvasItem editor
   */ 
  generateEditor() {}

  /**
   * Present editable final stage
   */
  generateFinale() {}

}

export default App;
