import React, { useState } from 'react';
import './App.css';

function App() {
  const [current, setScene] = useState("menu");

  const allNames = new Set(["menu", "catalog", "editor", "finale"]);
  var gameData = new Array();
  allNames.forEach(element => {
    gameData.push(<Scene type={element} currentScene={current} />);
  });

  return(
    <div className="App">
      {gameData.filter(scene => scene.props.type == scene.props.currentScene)}
    <button
        type="button"
        onClick={() => setScene("catalog")}
      >Blue</button>
    </div>
  );
}

class Scene extends React.Component {
  constructor(props) {
    super(props);
    this.handleChange = this.handleChange.bind(this);
    console.log(this);
  }

  handleChange(e) {;
    this.props.currentScene.bind(e);
  }

  render() {
    switch(this.props.type) {
      case "menu":
        return this.generateMenu();
      case "catalog":
        return generateCatalog();
      case "editor":
        return generateEditor();
      case "finale":
        return generateFinale();
      default:
        return;
    }
  }

  generateMenu() {
    return(
    <div className="titleScreen">
      <h1>Styledollz 2.0</h1>
      <button
        type="button"
        onClick={() => this.handleChange}
      >Blue</button>
    </div>
    );
  }
}
