import React, { useState } from 'react';
import './App.css';

function App() {
  const [current, setScene] = useState("menu");

  const allNames = new Set(["menu", "catalog", "editor", "finale"]);
  var gameData = new Array();
  allNames.forEach(element => {
    gameData.push(<Scene type={element} currentScene={current} setScene={setScene} />);
  });

  return(
    <div className="App">
      {gameData.filter(scene => scene.props.type == scene.props.currentScene)}
    </div>
  );
}

class Scene extends React.Component {
  constructor(props) {
    super(props);
  }

  render() {
    switch(this.props.type) {
      case "menu":
        return this.generateMenu();
      case "catalog":
        return this.generateCatalog();
      case "editor":
        return this.generateEditor();
      case "finale":
        return this.generateFinale();
      default:
        return;
    }
  }

  generateMenu() {
    console.log(this.props);
    return(
    <div className="titleScreen">
      <h1>Styledollz 2.0</h1>
      <button
        type="button"
        onClick={() => this.props.setScene("catalog")}
      >Blue</button>
    </div>
    );
  }

  generateCatalog() {
  return(
    <div className="catalogScreen">
      <div className='canvas'>
        THIS IS A CANVAS
      </div>
      <div className='catalog'>
        THIS IS A CATALOG
      </div>
    </div>
  );
  }

  generateEditor() {
    return;
  }

  generateFinale() {
    return;
  }
}

export default App;
