import React, { useState } from 'react';
import './App.css';

function App() {
  var gameData = initScenes();
  let tempScene = gameData.get("menu");
  tempScene.state.display = true;
  gameData.set("menu", tempScene);
  console.log(tempScene);
  return(
    <div className="App">
      <Scene />
    </div>
  );
}

function initScenes() {
  var gameData = new Map();
  const allScenes = new Set(["menu", "catalog", "editor", "finale"]);
  allScenes.forEach(element => {
    gameData.set(element, new Scene(element));
  });
  return gameData;
}

class Scene extends React.Component {
  constructor(type) {
    super();
    this.state = {
      display: false,
    };
    this.name = type;
  }

  dismount() {
    this.setState({display: false});
  }

  render() {
    console.log("fgkdfg");
    switch(this.name) {
      case "menu":
        return generateMenu();
      case "catalog":
        return generateCatalog();
      case "editor":
        return generateEditor();
      case "finale":
        return generateFinale();
      default:
        return;
    }
  }
}

/**
 * Present the main menu
 */
function generateMenu(gameData) {
  return(
  <div className="titleScreen">
    <h1>Styledollz 2.0</h1>
    <button onClick={this.dismount()}>Start!</button>
  </div>
  );
}

/**
  * Present the catalog and canvas
  */
function generateCatalog(gameData) {
  return(
    <div className="titleScreen">
      <h1>BIRTCHES</h1>
    </div>
  );
}

/**
 * Present canvasItem editor
 */ 
function generateEditor() {}

/**
 * Present editable final stage
 */
function generateFinale() {}

export default App;
