import './App.css';

function App() {
  var gameData = initScenes();
  gameData.currentScene = "menu";
  return(
    <div className="App">
      {drawScene(gameData.currentScene)}
    </div>
  );
}

/**
 * Draw the following appropriate scene
 */
function drawScene(currentScene) {
  console.log(currentScene.name);
  switch(currentScene.name) {
    case "menu":
      return generateMenu();
    case "catalog":
      return generateCatalog();
    case "editor":
      return generateEditor();
    case "finale":
      return generateFinale();
    default:
